ValidationRule.errorConditionFormula:
OR(
     AND(
         ISCHANGED( Sub_Estado__c ) ,  
         NOT(ISPICKVAL( Estatus_Proceso__c , 'Formalizado'))
     ),
     AND( ISCHANGED( Sub_Estado__c ) ,  
          ISPICKVAL( Estatus_Proceso__c , 'Formalizado'), 
          ISPICKVAL( Sub_Estatus_del_Proceso__c, 'En Firmas')
     )
)
